// Function node: Compute weekly_metrics for the CURRENT week (Mondayâ€“Sunday)
// Input: one item whose JSON has { activities: [...], wellness: [...] }

const { activities: acts, wellness: well } = items[0].json;

// 1ï¸âƒ£ Compute current week boundaries (Monday 00:00 to Sunday 23:59:59)
const today = new Date();
const day = today.getDay();               // 0=Sun, 1=Mon, â€¦ 6=Sat
const diffToMonday = (day + 6) % 7;
const monday = new Date(today);
monday.setDate(today.getDate() - diffToMonday);
monday.setHours(0,0,0,0);
const sunday = new Date(monday);
sunday.setDate(monday.getDate() + 6);
sunday.setHours(23,59,59,999);

// 2ï¸âƒ£ Helper: average over non-null
const avg = arr => arr.length
  ? arr.reduce((sum, v) => sum + v, 0) / arr.length
  : 0;

// 3ï¸âƒ£ Filter this weekâ€™s activities
const inWeek = a => {
  const d = new Date(a.start_date);
  return d >= monday && d <= sunday;
};
const weekActs = acts.filter(inWeek);

// 4ï¸âƒ£ Run, ride & strength metrics
const runActs      = weekActs.filter(a => a.type === 'Run');
const rideActs     = weekActs.filter(a => /Ride/.test(a.type));
const strengthActs = weekActs.filter(a => a.type === 'WeightTraining');

const runCount     = runActs.length;
const runDistance  = runActs.reduce((s,a) => s + (a.distance||0), 0);
const runTime      = runActs.reduce((s,a) => s + (a.elapsed_time||0), 0);

const rideCount    = rideActs.length;
const rideDistance = rideActs.reduce((s,a) => s + (a.distance||0), 0);
const rideTime     = rideActs.reduce((s,a) => s + (a.elapsed_time||0), 0);
const rideTrimp    = rideActs.reduce((s,a) => s + (a.trimp||0), 0);

const strengthCount = strengthActs.length;
const strengthTrimp = strengthActs.reduce((s,a) => s + (a.trimp||0), 0);

// 5ï¸âƒ£ Sessionâ€type counts by keyword in activity name
const vo2Sessions   = runActs.filter(a => /vo2/i.test(a.name)).length;
const tempoSessions = runActs.filter(a => /tempo|threshold|umbral/i.test(a.name)).length;
const longRuns      = runActs.filter(a => /long/i.test(a.name)).length;

// 6ï¸âƒ£ Wellness this week (filter by id = YYYY-MM-DD)
const wellWeek = well.filter(w => {
  const d = new Date(w.id);
  return d >= monday && d <= sunday;
});

const ctlMean        = avg(wellWeek.map(w=>w.ctl||0));
const atlMean        = avg(wellWeek.map(w=>w.atl||0));
const rampRateMean   = avg(wellWeek.map(w=>w.rampRate||0));
const restHrMean     = avg(wellWeek.map(w=>w.restingHR||0));
const stepsMean      = avg(wellWeek.map(w=>w.steps||0));
const sleepScoreMean = avg(wellWeek.map(w=>w.sleepScore||0));
const hrvMean        = avg(wellWeek.map(w=>w.hrv||0));

// 7ï¸âƒ£ Identify athleteId (from any activity)
const athleteId = acts.length
  ? acts[0].icu_athlete_id
  : null;

// 8ï¸âƒ£ Build weekly_metrics record
const weekStart = monday.toISOString().slice(0,10);
const weekEnd   = sunday.toISOString().slice(0,10);
const now       = new Date().toISOString();

return [{
  json: {
    athleteId,
    weekStart,
    weekEnd,

    runCount,
    runDistance,
    runTime,

    rideCount,
    rideDistance,
    rideTime,
    rideTrimp,

    vo2Sessions,
    tempoSessions,
    longRuns,

