/**
 * Build Telegram Message: formats the WeeklyPlan with date range and per-day details.
 */

// Extract the model content (or already-normalized plan)
const content = Array.isArray(items[0].json.choices)
  ? items[0].json.choices[0].message.content
  : items[0].json;

if (!content.activityPlan || !content.justification) {
  throw new Error('Missing activityPlan or justification in response.');
}

const plan = content.activityPlan;
const justs = content.justification;

const { weekStart, weekEnd, phase, objective } = plan.nextWeek || {};

// Build HTML message
let html = `ğŸƒâ€â™‚ï¸ <b>Weekly plan: ${weekStart} â†’ ${weekEnd}</b>
`;
html += `ğŸ“‚ <b>Phase:</b> ${phase}
`;
html += `ğŸ¯ <b>Objective:</b> ${objective}

`;

for (const d of plan.days) {
  html += `ğŸ“… <b>${d.day} (${d.date}) â€“ ${d.activity}</b>
`;
  html += `ğŸ“ ${d.distance_time || 'â€”'}
`;
  html += `ğŸ’“ ${d.intensity || 'â€”'}
`;
  html += `ğŸ¯ ${d.goal}
`;
  if (d.note) html += `ğŸ“ <i>${d.note}</i>
`;
  html += `
`;
}

html += `ğŸ“š <b>Justification</b>
`;
justs.forEach((j, i) => {
  html += `${i + 1}. ${j}
`;
});

const errors = Array.isArray(content.__errors) ? content.__errors : [];
const runEvent = content.runEvent || {
  runId: `${new Date().toISOString()}-${Math.random().toString(36).slice(2, 8)}`,
  status: 'success',
  attempt: content.__attempt ?? null,
  weekStart: weekStart || null,
  weekEnd: weekEnd || null,
  errorCount: errors.length,
  errors,
  createdAt: new Date().toISOString()
};

return [{
  json: Object.assign({}, content, runEvent, {
    runEvent,
    htmlMessage: html.trim()
  })
}];

const attempt = 0;

const raw = Array.isArray(items[0].json.choices)
  ? items[0].json.choices[0].message.content
  : items[0].json;

let parsed = raw;
let parseError = null;

if (typeof raw === 'string') {
  try {
    parsed = JSON.parse(raw);
  } catch (err) {
    parseError = err && err.message ? err.message : String(err);
    parsed = null;
  }
}

const errors = [];

if (parseError) {
  errors.push(`invalid_json: ${parseError}`);
}

const isObject = value => value && typeof value === 'object' && !Array.isArray(value);
const plan = isObject(parsed) ? parsed : {};

const dateRe = /^\d{4}-\d{2}-\d{2}$/;
const dayRe = /^(Lunes|Martes|Miercoles|Mi\u00e9rcoles|Jueves|Viernes|Sabado|S\u00e1bado|Domingo)$/;
const intensityRe = /^(?:-|--|\u2014|.*\b(Z[1-5]|interval|Interval|VO2|Tempo|tempo|Umbral|umbral|Threshold|threshold|Easy|easy|Recovery|recovery)\b.*)$/;

const reqString = (obj, key, path) => {
  const value = obj ? obj[key] : undefined;
  if (typeof value !== 'string' || !value.trim()) {
    errors.push(`${path}.${key} missing`);
    return false;
  }
  return true;
};

if (plan.schema_version !== '1.0') {
  errors.push('schema_version must be "1.0"');
}

if (!isObject(plan.activityPlan)) {
  errors.push('activityPlan must be object');
}

const nextWeek = plan.activityPlan && plan.activityPlan.nextWeek;
if (!isObject(nextWeek)) {
  errors.push('activityPlan.nextWeek must be object');
} else {
  reqString(nextWeek, 'phase', 'activityPlan.nextWeek');
  reqString(nextWeek, 'objective', 'activityPlan.nextWeek');
  if (!reqString(nextWeek, 'weekStart', 'activityPlan.nextWeek') || !dateRe.test(nextWeek.weekStart)) {
    errors.push('activityPlan.nextWeek.weekStart must be YYYY-MM-DD');
  }
  if (!reqString(nextWeek, 'weekEnd', 'activityPlan.nextWeek') || !dateRe.test(nextWeek.weekEnd)) {
    errors.push('activityPlan.nextWeek.weekEnd must be YYYY-MM-DD');
  }
}

const days = plan.activityPlan && plan.activityPlan.days;
if (!Array.isArray(days)) {
  errors.push('activityPlan.days must be array');
} else if (days.length !== 7) {
  errors.push('activityPlan.days must have length 7');
} else {
  days.forEach((day, idx) => {
    const path = `activityPlan.days[${idx}]`;
    if (!isObject(day)) {
      errors.push(`${path} must be object`);
      return;
    }
    if (!reqString(day, 'day', path) || !dayRe.test(day.day)) {
      errors.push(`${path}.day must be a valid weekday`);
    }
    if (!reqString(day, 'date', path) || !dateRe.test(day.date)) {
      errors.push(`${path}.date must be YYYY-MM-DD`);
    }
    reqString(day, 'activity', path);
    reqString(day, 'distance_time', path);
    if (!reqString(day, 'intensity', path) || !intensityRe.test(day.intensity)) {
      errors.push(`${path}.intensity must include a zone/label or be '-'/'--'/'â€”'`);
    }
    reqString(day, 'goal', path);
  });
}


// Guardrails (hard rules)
if (Array.isArray(days) && days.length === 7) {
  const normalizeDay = (value) => (value || '')
    .toLowerCase()
    .replace(/[Ã¡Ã Ã¤]/g, 'a')
    .replace(/[Ã©Ã¨Ã«]/g, 'e')
    .replace(/[Ã­Ã¬Ã¯]/g, 'i')
    .replace(/[Ã³Ã²Ã¶]/g, 'o')
    .replace(/[ÃºÃ¹Ã¼]/g, 'u')
    .replace(/Ã±/g, 'n');
  const activityText = (day) => `${day.activity || ''} ${day.goal || ''} ${day.note || ''}`.toLowerCase();
  const intensityText = (day) => (day.intensity || '').toLowerCase();
  const containsToken = (text, tokens) => tokens.some((token) => text.includes(token));

  const hardTokens = ['z4', 'z5', 'vo2', 'interval', 'umbral', 'tempo', 'threshold'];
  const easyTokens = ['z1', 'z2', 'easy', 'recovery', 'recuper', 'suave'];
  const restTokens = ['descanso', 'rest', 'off'];
  const longTokens = ['tirada', 'larga', 'long'];
  const gymTokens = ['gimnasio', 'gym', 'fuerza', 'strength'];

  const isHard = (day) => containsToken(intensityText(day), hardTokens) || containsToken(activityText(day), hardTokens);
  const isEasy = (day) => containsToken(intensityText(day), easyTokens) || containsToken(activityText(day), easyTokens);
  const isRest = (day) => containsToken(activityText(day), restTokens) || ['-', '--', 'â€”', 'â€“'].includes((day.intensity || '').trim());
  const normalizedDays = days.map((day) => normalizeDay(day.day));
  const uniqueDays = new Set(normalizedDays);
  if (uniqueDays.size !== 7) {
    errors.push('guardrail: days must cover all weekdays exactly once');
  }
  const requiredDays = ['lunes', 'martes', 'miercoles', 'jueves', 'viernes', 'sabado', 'domingo'];
  requiredDays.forEach((name) => {
    if (!uniqueDays.has(name)) {
      errors.push(`guardrail: missing weekday ${name}`);
    }
  });

  const isLong = (day) => containsToken(activityText(day), longTokens);

  const hardCount = days.filter(isHard).length;
  if (hardCount > 2) {
    errors.push(`guardrail: too many hard sessions (${hardCount} > 2)`);
  }

  for (let i = 1; i < days.length; i += 1) {
    if (isHard(days[i]) && isHard(days[i - 1])) {
      errors.push(`guardrail: back-to-back hard sessions (days ${i} and ${i + 1})`);
    }
  }

  const hasRecovery = days.some((day) => isRest(day) || isEasy(day));
  if (!hasRecovery) {
    errors.push('guardrail: must include at least one rest or recovery day');
  }

  const longCount = days.filter(isLong).length;
  if (longCount > 1) {
    errors.push('guardrail: only one long run per week');
  }

  days.forEach((day, idx) => {
    if (isLong(day) && isHard(day)) {
      errors.push(`guardrail: long run cannot be hard intensity (day ${idx + 1})`);
    }
  });

  const gymDays = ['martes', 'jueves', 'sabado'];
  gymDays.forEach((name) => {
    const day = days.find((d) => normalizeDay(d.day) === name);
    if (day && !containsToken(activityText(day), gymTokens)) {
      errors.push(`guardrail: ${name} should include gym/strength`);
    }
  });
}
const valid = errors.length == 0;

const output = isObject(plan) ? JSON.parse(JSON.stringify(plan)) : {};
output.__valid = valid;
