/**
 * Build Telegram Message: formatea el JSON de OpenAI a√±adiendo rango de fechas
 * y fecha concreta de cada d√≠a, sin depender de m√©tricas externas.
 */

 // 1Ô∏è‚É£ Extraer el contenido generado por OpenAI
const content = Array.isArray(items[0].json.choices)
  ? items[0].json.choices[0].message.content
  : items[0].json;

if (!content.activityPlan || !content.justification) {
  throw new Error("No se encontr√≥ `activityPlan` o `justification` en la respuesta.");
}

const plan  = content.activityPlan;
const justs = content.justification;

// 2Ô∏è‚É£ Cabecera: rango de la semana
const { weekStart, weekEnd, phase, objective } = plan.nextWeek;

// 3Ô∏è‚É£ Construir el HTML
let html = `üèÉ‚Äç‚ôÇÔ∏è <b>Plan semanal: ${weekStart} ‚Üí ${weekEnd}</b>\n`;
html += `üìÇ <b>Fase:</b> ${phase}\n`;
html += `üéØ <b>Objetivo:</b> ${objective}\n\n`;

for (const d of plan.days) {
  html += `üìÖ <b>${d.day} (${d.date}) ‚Äì ${d.activity}</b>\n`;
  html += `üìè ${d.distance_time || '‚Äî'}\n`;
  html += `üíì ${d.intensity || '‚Äî'}\n`;
  html += `üéØ ${d.goal}\n`;
  if (d.note) html += `üìù <i>${d.note}</i>\n`;
  html += `\n`;
}

html += `üìö <b>Justificaci√≥n</b>\n`;
justs.forEach((j, i) => {
  html += `${i + 1}. ${j}\n`;
});

// 4Ô∏è‚É£ Salida para el nodo Telegram
return [{
  json: {
    htmlMessage: html.trim()
  }
}];
